import consumer from "./consumer"

$(function() {
const chatChannel = consumer.subscriptions.create({
    channel: "ProductChannel",
    product_id: $('#messages').data('product-id')
  }, {
  connected() {
    // Called when the subscription is ready for use on the server
  },

  disconnected() {
    // Called when the subscription has been terminated by the server
  },

  received(data) {
    // Called when there's incoming data on the websocket for this channel
    var message = `<div class="toast" role="alert" aria-live="assertive" aria-atomic="true">
                <div class="toast-header">
                  <img src="..." class="rounded mr-2" alt="...">
                  <strong class="mr-auto">Bootstrap</strong>
                  <small class="text-muted">just now</small>
                </div>
                <div class="toast-body">
                  ${data['message']}
                </div>
               </div>`;
    var messages = getElementById('messages');
    console.log(messages);
    $('#messages').insertAdjacentHTML('beforeend', message);
  },

  speak: function(message) {
    return this.perform('speak', {message: message});
  }
});

  $(document).on('click', '#message_button', function(){
    const value = $('#message_field').val();
    chatChannel.speak(value);
    document.getElementById('message_field').value = '';
    $('#message_field').focus();
  });
});
